const extractTextFromResponse = (response) => {
  if (Array.isArray(response) && response.length > 0) {
    const firstItem = response[0];
    const textPart = firstItem.content?.parts?.[0];
    if (textPart?.text) return textPart.text.trim();
  }
  if (response.candidates && response.candidates.length > 0) {
    const textPart = response.candidates[0].content?.parts?.find(part => part.text);
    if (textPart?.text) return textPart.text.trim();
  }
  if (response.content?.parts?.length > 0) {
    const textPart = response.content.parts[0];
    if (textPart?.text) return textPart.text.trim();
  }
  return null;
};

const cleanJsonText = (text) => {
  let cleaned = text.replace(/^```json\s*/i, '').replace(/^```\s*/i, '').replace(/\s*```$/i, '').trim();
  const jsonMatch = cleaned.match(/\{[\s\S]*\}/);
  return jsonMatch ? jsonMatch[0] : cleaned;
};

const parseMetadata = (text) => {
  try {
    const meta = JSON.parse(text);
    if (!meta || typeof meta !== 'object') {
      return { error: 'Parsed result is not an object', raw: meta };
    }
    return {
      youtube_title: meta.youtube_title ? String(meta.youtube_title).trim() : '',
      youtube_description: meta.youtube_description ? String(meta.youtube_description).trim() : '',
      raw: meta
    };
  } catch (e) {
    return { error: `Parse error: ${e.message}`, rawText: text.substring(0, 400) };
  }
};

const response = $input.item.json;

if (response.error) {
  return {
    json: {
      youtube_title: '',
      youtube_description: '',
      error: `API Error: ${response.error.message || JSON.stringify(response.error)}`,
      valid: false,
      raw: response
    }
  };
}

const rawText = extractTextFromResponse(response);
if (!rawText) {
  return {
    json: {
      youtube_title: '',
      youtube_description: '',
      error: 'No text found in response',
      valid: false,
      raw: response
    }
  };
}

const cleanedText = cleanJsonText(rawText);
const parsed = parseMetadata(cleanedText);

if (parsed.error) {
  return {
    json: {
      youtube_title: '',
      youtube_description: '',
      error: parsed.error,
      valid: false,
      rawText: parsed.rawText,
      raw: parsed.raw
    }
  };
}

return {
  json: {
    youtube_title: parsed.youtube_title,
    youtube_description: parsed.youtube_description,
    error: null,
    valid: !!(parsed.youtube_title && parsed.youtube_description),
    raw: parsed.raw
  }
};

const generateUuid = () => {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, c => {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
};

const row = $('Get Video Information').item.json;

if (!row.id || !row.video_url) {
  throw new Error('Missing required field: id or video_url');
}

return {
  json: {
    id: row.id,
    video_title: row.video_title,
    video_url: row.video_url,
    driveUuid: generateUuid()
  }
};

const parseYouTubeMetadata = (stdout) => {
  if (!stdout) return { title: '', description: '' };
  const parts = stdout.trim().split('|');
  if (parts.length < 3) return { title: '', description: '' };
  return {
    title: parts[0].trim(),
    description: parts[2].trim().substring(0, 500)
  };
};

const row = $('Get Video Information').item.json;
const extractResult = $('Extract YouTube Metadata').item.json;
const extractUrl = $('Extract Video URL').item.json;

const ytMeta = parseYouTubeMetadata(extractResult.stdout);
const videoTitle = row.video_title && row.video_title.trim() ? row.video_title.trim() : ytMeta.title;

if (!videoTitle) {
  throw new Error('Cannot extract video title from YouTube');
}

return {
  json: {
    ...extractUrl,
    video_title: videoTitle,
    youtube_metadata: {
      title: ytMeta.title,
      description: ytMeta.description
    }
  }
};

for (const item of $input.all()) {
  delete item.binary;
}
return $input.all();

const normalizeBoolean = (value) => {
  const normalized = String(value).trim().toLowerCase();
  return normalized === 'true' || normalized === '1' || normalized === 'yes';
};

const fixMimeType = (mimeType) => {
  if (mimeType === 'application/mp4' || mimeType === 'application/x-mp4') {
    return 'video/mp4';
  }
  return mimeType;
};

const videoInfo = $('Get Video Information').first().json;
const currentItem = $input.item;

if (!currentItem.binary || !currentItem.binary.data) {
  throw new Error('No video binary found from "Read Processed Video For YouTube"');
}

const binaryData = currentItem.binary.data;
if (!binaryData.data && !binaryData.mimeType) {
  throw new Error('Invalid binary data structure');
}

return {
  json: {
    ...videoInfo,
    id: videoInfo.id || currentItem.json.id,
    enable_youtube_upload_normalized: String(videoInfo.enable_youtube_upload).trim().toLowerCase(),
    enable_youtube_upload_should_upload: normalizeBoolean(videoInfo.enable_youtube_upload)
  },
  binary: {
    data: {
      ...binaryData,
      mimeType: fixMimeType(binaryData.mimeType)
    }
  }
};

for (const item of $input.all()) {
  delete item.binary;
}
return $input.all();

const STATUS = {
  SUCCESS: 'success',
  ERROR: 'error',
  NONE: 'none'
};

const getNodeResult = (nodeName) => {
  const node = $(nodeName);
  if (!node || !node.isExecuted) return null;
  return node.first();
};

const extractUploadId = (uploadItem) => {
  if (!uploadItem || uploadItem.error || !uploadItem.json) return null;
  const json = uploadItem.json;
  if (Array.isArray(json) && json.length > 0) return json[0].uploadId;
  if (typeof json === 'object' && json !== null) return json.uploadId;
  return null;
};

const extractYouTubeLink = () => {
  const uploadItem = getNodeResult('Upload To YouTube');
  if (!uploadItem) return '';
  const videoId = extractUploadId(uploadItem);
  if (!videoId) return '';
  const trimmedId = String(videoId).trim();
  return trimmedId ? `https://www.youtube.com/watch?v=${trimmedId}` : '';
};

const checkNodeError = (nodeName) => {
  const item = getNodeResult(nodeName);
  if (!item) return null;
  if (item.error) return item.error.message;

  if (item.json && item.json.stderr) {
    const stderr = String(item.json.stderr);

    if (stderr.includes('ffmpeg version')) {
      const lines = stderr.split('\n');
      const errorLines = lines.filter(line => 
        line.toLowerCase().includes('error') || 
        line.toLowerCase().includes('failed') ||
        line.toLowerCase().includes('cannot') ||
        line.toLowerCase().includes('invalid')
      );
      if (errorLines.length > 0) {
        return errorLines.join('\n').substring(0, 300);
      }
      if (item.json.exitCode && item.json.exitCode !== 0) {
        return 'FFmpeg execution failed';
      }
      return null;
    }

    if (stderr.includes('--') && (stderr.includes('wget') || stderr.includes('HTTP'))) {
      const lines = stderr.split('\n');
      const errorLines = lines.filter(line => 
        line.toUpperCase().includes('ERROR') || 
        line.toLowerCase().includes('failed') ||
        line.includes('404') ||
        line.includes('403') ||
        line.includes('500') ||
        line.toLowerCase().includes('not found')
      );
      if (errorLines.length > 0) {
        return errorLines.join('\n').substring(0, 300);
      }
      if (item.json.exitCode && item.json.exitCode !== 0) {
        return 'Download failed';
      }
      return null;
    }

    return stderr.substring(0, 300);
  }

  if (item.json && item.json.exitCode && item.json.exitCode !== 0) {
    return 'Node execution failed';
  }

  return null;
};

const getDriveLinkFromNode = () => {
  const driveLinkItem = getNodeResult('Get Drive Link');
  if (!driveLinkItem || driveLinkItem.error || !driveLinkItem.json || !driveLinkItem.json.stdout) return null;
  return String(driveLinkItem.json.stdout).trim();
};

const getTimestamp = () => {
  return new Date().toLocaleString('vi-VN', {
    timeZone: 'Asia/Ho_Chi_Minh',
    year: 'numeric',
    month: '2-digit',
    day: '2-digit',
    hour: '2-digit',
    minute: '2-digit',
    second: '2-digit'
  });
};

const getCurrentVideoInfo = () => {
  const currentItem = $input.item;

  if (currentItem && currentItem.json && currentItem.json.id) {
    return currentItem.json;
  }

  const getVideoInfoAfterUpload = $('Get Video Information After Upload');
  if (getVideoInfoAfterUpload && getVideoInfoAfterUpload.isExecuted) {
    const item = getVideoInfoAfterUpload.first();
    if (item && item.json && item.json.id) {
      return item.json;
    }
  }

  const getVideoInfoForCheck = $('Get Video Info For YouTube Check');
  if (getVideoInfoForCheck && getVideoInfoForCheck.isExecuted) {
    const item = getVideoInfoForCheck.first();
    if (item && item.json && item.json.id) {
      return item.json;
    }
  }

  const mergeData = $('Merge Data For YouTube Check');
  if (mergeData && mergeData.isExecuted) {
    const item = mergeData.first();
    if (item && item.json && item.json.id) {
      return item.json;
    }
  }

  const getVideoInfo = $('Get Video Information');
  if (getVideoInfo && getVideoInfo.isExecuted) {
    const allItems = getVideoInfo.all();
    if (allItems && allItems.length > 0) {
      const currentId = currentItem?.json?.id;
      if (currentId) {
        const matchedItem = allItems.find(item => item.json && item.json.id === currentId);
        if (matchedItem) return matchedItem.json;
      }
      const loopItems = $('Loop Over Items');
      if (loopItems && loopItems.isExecuted) {
        const loopItem = loopItems.first();
        if (loopItem && loopItem.json && loopItem.json.id) {
          const matchedItem = allItems.find(item => item.json && item.json.id === loopItem.json.id);
          if (matchedItem) return matchedItem.json;
        }
      }
      return allItems[0].json;
    }
  }

  return null;
};

const videoInfo = getCurrentVideoInfo() || $('Get Video Information').first().json;
const youtubeLink = extractYouTubeLink();
const driveLink = getDriveLinkFromNode() || videoInfo.processed_video_drive_link;

const checkAlreadyProcessed = () => {
  const setResultStatusNode = $('Set Result Status');
  if (!setResultStatusNode || !setResultStatusNode.isExecuted) return false;
  const allResults = setResultStatusNode.all();
  if (!allResults || allResults.length === 0) return false;
  const currentId = videoInfo.id;
  if (!currentId) return false;
  const processedIds = allResults
    .map(item => item.json && item.json.id)
    .filter(id => id && id === currentId);
  return processedIds.length > 0;
};

if (checkAlreadyProcessed()) {
  return {
    json: {
      status: STATUS.NONE,
      errorMessage: '',
      id: videoInfo.id,
      videoTitle: videoInfo.video_title || videoInfo.youtube_title,
      videoUrl: videoInfo.video_url,
      driveLink,
      youtubeLink,
      publishStatus: videoInfo.publish_status ? String(videoInfo.publish_status).toLowerCase() : '',
      timestamp: getTimestamp(),
      skip: true
    }
  };
}

const errorNodes = ['Process Video', 'Download Video', 'Upload to Drive', 'Get Drive Link', 'Insert Background Intro', 'Download Processed Video From Drive', 'Extract Frame', 'Read Processed Video For YouTube', 'Merge Data For YouTube Check'];
const uploadError = checkNodeError('Upload To YouTube');
const execErrors = errorNodes.map(node => checkNodeError(node)).filter(e => e);
const firstError = uploadError || execErrors[0];

const status = firstError ? STATUS.ERROR : (youtubeLink ? STATUS.SUCCESS : STATUS.NONE);
const errorMessage = firstError ? String(firstError).substring(0, 300) : '';

return {
  json: {
    status,
    errorMessage,
    id: videoInfo.id,
    videoTitle: videoInfo.video_title || videoInfo.youtube_title,
    videoUrl: videoInfo.video_url,
    driveLink,
    youtubeLink,
    publishStatus: videoInfo.publish_status ? String(videoInfo.publish_status).toLowerCase() : '',
    timestamp: getTimestamp()
  }
};

