const videoInfo = $('Merge Data For YouTube Check').first().json;
const videoData = $('Merge Data For YouTube Check').first();
const currentItem = $input.item;

const result = {
  json: { ...videoInfo },
  binary: {
    data: videoData.binary.data
  }
};

if (currentItem.binary && currentItem.binary.data && currentItem.binary.data.mimeType) {
  const thumbBinary = currentItem.binary.data;
  if (thumbBinary.mimeType.startsWith('image/')) {
    result.binary.thumbnail = thumbBinary;
    result.json.has_thumbnail = true;
  }
}

return result;
